    '}\n',
    'return toolbar_t',
    }
    local mystring = table.concat(code)
    my_code = assert(loadstring(mystring))()
    eu_core.save_file(bar_file, mystring)
  else
    my_code = assert(dofile(bar_file))
  end
  local m_len = tonumber(#my_code)
  if (m_len ~= nil) then
    local m_bar = eu_core.ffi.new("eue_toolbar[?]", m_len, {})
    for i = 0, m_len - 1 do
      local m_ptr = eu_core.ffi.cast('eue_toolbar *', m_bar[i])
      m_ptr.imsg = eu_core.ffi.cast('int', my_code[i+1][1])
      m_ptr.icmd = eu_core.ffi.cast('int', my_code[i+1][2])
      if (my_code[i+1][3] ~= nil and #my_code[i+1][3] < 4096) then
        -- ffi.copy没必要指明第三个参数
        eu_core.ffi.copy(m_ptr.isvg, my_code[i+1][3])
      else
        eu_core.ffi.fill(m_ptr.isvg, 4096)
      end
    end
    if (not eu_core.euapi.eu_toolbar_ptr(m_bar, m_len)) then
      do return 1 end
    end
  end
  return 0
end

return eu_toolbar
  